package 변수;

public class 변수선언확인문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 23;
		double num2 = 165;
		char c = '서';
		boolean b = false;
		
//		기본데이터X -> 너무 많이 쓰기 때문에 기본형 데이터처럼 사용가
		String name = "홍길동";
		
		System.out.println("내 나이는 " + num);
		System.out.println("내 키는" + num2);
		System.out.println("내 성씨는 " + c);
		System.out.println("나의 저녁은 " + b);
		System.out.println("내 이름은 " + name);
	}

}
